import React, { Component } from "react";
import SongDDataService from "../services/songdata.service";
export default class AddSongData extends Component {
    constructor(props) {
        super(props);
        this.onChangeArea = this.onChangeArea.bind(this);
        this.onChangeClass = this.onChangeClass.bind(this);
        this.saveSongData = this.saveSongData.bind(this);
        this.newSongData = this.newSongData.bind(this);
        this.state = {
            id: null,
            items: "",
            area: "",
            class: 0,
            limit: 0,
            offset: 0,
            previous: "",
            href: "",
            next: ""

        };
    }
    onChangeArea(e) {
        this.setState({
            area: e.target.value
        });
    }
    onChangeClass(e) {
        this.setState({
            class: e.target.value
        });
    }
    saveSongData() {
        var data = {
            items: this.state.items,
            area: this.state.area,
            class: this.state.class,
            limit: this.state.limit,
            offset: this.state.offset,
            previous: this.state.previous,
            href: this.state.href,
            next: this.state.next
        };
        SongDDataService.create(data)
            .then(response => {
                this.setState({
                    id: response.data.id
                    //title: response.data.title,
                    //description: response.data.description,
                    //published: response.data.published,
                    //submitted: true
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }
    newSongData() {
        this.setState({
            id: null,
            items: "",
            area: "",
            class: 0,
            limit: 0,
            offset: 0,
            previous: "",
            href: "",
            next: ""
        });
    }
    render() {
        return (
            <div className="submit-form">
                {this.state.submitted ? (
                    <div>
                        <h4>You submitted successfully!</h4>
                        <button className="btn btn-success" onClick={this.newTutorial}>
                            Add
                        </button>
                    </div>
                ) : (
                    <div>
                        <select id="spotifyCols" autoComplete="off">
                            <option defaultValue disabled="">Columns:</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                            <option value="6">6</option>
                            <option value="7">7</option>
                        </select>
                        <select id="spotifyPeriod" autoComplete="off">
                            <option defaultValue disabled="">Área de estudo</option>
                            <option value="long_term">Ciências Humanas</option>
                            <option value="short_term">Ciências Exatas e da Terra</option>
                            <option value="medium_term">Ciências Sociais</option>
                        </select>
                        <button onClick={this.saveSongData} className="next action-button">Autenticar
                            <i className="fa fa-spotify fa-right aria-hidden=true"></i>
                        </button>



                        <div className="form-group">
                            <label htmlFor="title">Title</label>
                            <input
                                type="text"
                                className="form-control"
                                id="title"
                                required
                                value={this.state.title}
                                onChange={this.onChangeTitle}
                                name="title"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="description">Description</label>
                            <input
                                type="text"
                                className="form-control"
                                id="description"
                                required
                                value={this.state.description}
                                onChange={this.onChangeDescription}
                                name="description"
                            />
                        </div>
                        <button onClick={this.saveSongData} className="btn btn-success">
                            Submit
                        </button>
                    </div>
                )}
            </div>
        );
    }
}
