import React, { useState, useEffect } from "react";
import axios from "axios";
import SongDDataService from "../services/songdata.service";


const optionsArea = [
    {
        label: "Ciências Humanas",
        value: "humanas",
    },
    {
        label: "Ciências Exatas da Terra",
        value: "exaras",
    },
    {
        label: "Ciências Biológicas",
        value: "biologicas",
    },
    {
        label: "Engenharias",
        value: "eng",
    },
    {
        label: "Ciências da Saúde",
        value: "saude",
    },
    {
        label: "Ciências Agrárias",
        value: "agrarias",
    },
    {
        label: "Linguística, Letras e Artes",
        value: "letras",
    },
    {
        label: "Ciências Sociais Aplicadas",
        value: "sociais",
    },
    {
        label: "Outras",
        value: "outras",
    }
];

const optionsGrau = [
    {
        label: "Graduação",
        value: "graduacao",
    },
    {
        label: "Especialização/MBA",
        value: "espec",
    },
    {
        label: "Mestrado",
        value: "mestrado",
    },
    {
        label: "Doutorado",
        value: "doutorado",
    },
];

var generateRandomString = function (length) {
    var text = '';
    var possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

    for (var i = 0; i < length; i++) {
        text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
};


function Login() {
    const CLIENT_ID = "55b9c926505a44b9b685faaec5b28633"
    const REDIRECT_URI = "http://localhost:8081/callback"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"
    const CLIENT_SECRET = '75a0d8b2918c4159a1cb68bea678d562'
    const SCOPE = 'user-top-read';

    const [token, setToken] = useState("")
    const [searchKey, setSearchKey] = useState("")
    const [artists, setArtists] = useState([])
    const [Top1, setTop1] = useState([])
    const [area, setArea] = useState("")
    const [grau, setGrau] = useState("")

    // const getToken = () => {
    //     let urlParams = new URLSearchParams(window.location.hash.replace("#","?"));
    //     let token = urlParams.get('access_token');
    // }

    useEffect(() => {
        const hash = window.location.hash
        console.log(`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}&state=${generateRandomString(16)}`)
        let token = window.localStorage.getItem("token")

        // getToken()

        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]
            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }

        setToken(token)
    }, [])

    const onChangeArea = async (e) => {
        setArea(e.target.value)
        console.log('onChangeArea ' + e.target.value)
    }

    const onChangeGrau = async (e) => {
        setGrau(e.target.value)
        console.log('onChangeGrau ' + e.target.value)
    }

    const logout = () => {
        setToken("")
        window.localStorage.removeItem("token")
    }

    const searchTop1 = async (e) => {
        e.preventDefault()
        const { data } = await axios.get("https://api.spotify.com/v1/me/top/tracks", {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        setTop1(data.items)
        return 'Finalizado'
    }

    const renderArtists = () => {
        return artists.map(artist => (
            <div key={artist.id}>
                {artist.images.length ? <img width={"100%"} src={artist.images[0].url} alt="" /> : <div>No Image</div>}
                {artist.name}
            </div>
        ))
    }
    const saveSongData = async (e) => {
        e.preventDefault()
        const { songs } = await axios.get("https://api.spotify.com/v1/me/top/tracks", {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        setTop1(songs)

        var data = {
            area: area,
            grau: grau,
            items: songs
        };
        console.log('saveSongData ' + data)
        //SpotifyService.getAuth()
        SongDDataService.create(data)
            .then(response => {
                this.setState({
                    id: response.data.id
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    return (
        <div>
            {token ?
                <div>
                    <select id="spotifyCols" autoComplete="off" defaultValue='Grau' onChange={onChangeGrau}>
                        {optionsGrau.map((option) => (
                            <option key={option.value} value={option.value}>{option.label}</option>
                        ))}
                    </select>
                    <select id="spotifyPeriod" autoComplete="off" defaultValue='Área de Estudo' onChange={onChangeArea}>
                        {optionsArea.map((option) => (
                            <option key={option.value} value={option.value}>{option.label}</option>
                        ))}
                    </select>
                    <button onClick={saveSongData} className="next action-button">Finalizar
                    </button>
                    <button onClick={logout} style={{ visibility: token ? 'visible' : 'hidden' }} className="next action-button">Cancelar</button>
                </div>
                : <h2>Please login</h2>
            }
            {!token ?
                <a className="next action-button abutton" href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}&state=${generateRandomString(16)}`}>
                    Autenticar <i className="fa fa-spotify fa-right aria-hidden=true"></i></a>
                : null}
        </div>
    );
}
export default Login;
