import React, { Component, useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";


import AddSongData from "./components/add-songdata.component";
import SongData from "./components/songdata.component";
import SongDataList from "./components/songdatas-list.component";


function Login() {
    const CLIENT_ID = "b39c9c2f4fa346a69e4cdbcafefd5185"
    const REDIRECT_URI = "http://localhost:3001"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"

    const [token, setToken] = useState("")
    const [searchKey, setSearchKey] = useState("")
    const [artists, setArtists] = useState([])

    // const getToken = () => {
    //     let urlParams = new URLSearchParams(window.location.hash.replace("#","?"));
    //     let token = urlParams.get('access_token');
    // }

    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")

        // getToken()


        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }

        setToken(token)

    }, [])

    const logout = () => {
        setToken("")
        window.localStorage.removeItem("token")
    }

    const searchArtists = async (e) => {
        e.preventDefault()
        const { data } = await axios.get("https://api.spotify.com/v1/search", {
            headers: {
                Authorization: `Bearer ${token}`
            },
            params: {
                q: searchKey,
                type: "artist"
            }
        })

        setArtists(data.artists.items)
    }

    const renderArtists = () => {
        return artists.map(artist => (
            <div key={artist.id}>
                {artist.images.length ? <img width={"100%"} src={artist.images[0].url} alt="" /> : <div>No Image</div>}
                {artist.name}
            </div>
        ))
    }

    return (
        <div className="App">
            <header className="App-header">
                <h1>Spotify React</h1>
                {!token ?
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login
                        to Spotify</a>
                    : <button onClick={logout}>Logout</button>}

                {token ?
                    <form onSubmit={searchArtists}>
                        <input type="text" onChange={e => setSearchKey(e.target.value)} />
                        <button type={"submit"}>Search</button>
                    </form>

                    : <h2>Please login</h2>
                }

                {renderArtists()}

            </header>
        </div>
    );
}

class App extends Component {
    render() {
        return (
            <div className="wrapper container-fluid">
                <div className="app-container">
                    <div className="row text-center" id="title_div">
                        <h2>EscutaUFMG
                            <span className="lighter"> com Spotify </span>
                            <i className="fa fa-spotify fa-right" aria-hidden="true"></i>
                        </h2>
                    </div>
                    <div>
                        <form id="msform">
                            <fieldset>
                                <h2 className="fs-title">Isso não irá expor seus dados ou hackear sua conta.</h2>
                                <h3 className="fs-subtitle"><a className="link"
                                    href="https://medium.com/@bauus/no-my-collage-generator-for-spotify-cannot-hack-your-spotify-7d44cd68b1d1?sk=ef58ff48ac46a1fec575700f4844d7eb">
                                    (saiba mais)</a></h3>
                                <AddSongData />
                                <Example />
                            </fieldset>
                        </form>
                    </div>
                </div>
                <nav className="footer">
                    <p>
                        <a className="link" href="https://open.spotify.com/user/aapw?si=-o1yIHjYTHiYZnbuRUi3iA">Siga-me</a> no Spotify
                        <i className="fa fa-spotify" aria-hidden="true"></i>
                    </p>
                    <p id="copyright">Copyright © Otávio Venâncio 2022</p>
                </nav>
            </div>
        );
    }
}
export default App;

