import axios from "axios";
export default axios.create({
    baseURL: 'https://accounts.spotify.com/api',
    headers: {
        'Authorization': 'Basic ' + (Buffer.from(process.env.CLIENT_ID + ':' + process.env.CLIENT_SECRET).toString('base64'))
    },
    json: true
});
