import http from "../http-spotify";

class SpotifyService {
    getAuth() {
        return http.get("/songdatas");
    }
    getTopData(id) {
        return http.get(`/songdatas/${id}`);
    }
    create(data) {
        return http.post("/songdatas", data);
    }
    updateToken() { //app.get('/refresh_token', function (req, res) {
        // requesting access token from refresh token 
        function getHashParams() {
            var hashParams = {};
            var e, r = /([^&;=]+)=?([^&;]*)/g,
                q = window.location.hash.substring(1);
            while (e = r.exec(q)) {
                hashParams[e[1]] = decodeURIComponent(e[2]);
            }
            return hashParams;
        }
        var params = getHashParams();
        var access_token = params.access_token,
            refresh_token = params.refresh_token,
            error = params.error;
        var authOptions = {
            url: 'https://accounts.spotify.com/api/token',
            headers: { 'Authorization': 'Basic ' + (Buffer.from(process.env.CLIENT_ID + ':' + process.env.CLIENT_SECRET).toString('base64')) },
            form: {
                grant_type: 'refresh_token',
                refresh_token: refresh_token
            },
            json: true
        };

        /*http.post(authOptions, function (error, response, body) {
            if (!error && response.statusCode === 200) {
                var access_token = body.access_token;
                res.send({
                    'access_token': access_token
                });
            }
        });*/

        http.post('/refresh_token', {
            data: {
                'refresh_token': refresh_token
            }
        })
            .then((response) => {
                console.log(response) //dispatch({ type: FOUND_USER, data: response.data[0] })
            })
            .catch((error) => {
                console.log(error)//dispatch({ type: ERROR_FINDING_USER })
            })

        //return response.access_token//http.put(`/songdatas/${id}`, data);
    }
}
export default new SpotifyService();
